declare module 'react-native-vector-icons/FontAwesome' {
    // Este bloque de código está declarando un módulo para 'react-native-vector-icons/FontAwesome'.
    // Es una declaración personalizada que indica a TypeScript cómo manejar el módulo 'FontAwesome' de 'react-native-vector-icons'.
    // La biblioteca 'react-native-vector-icons' ofrece una variedad de iconos para usar en aplicaciones React Native.
    
    // Aquí se está diciendo que el módulo de 'FontAwesome' exporta por defecto el componente 'Icon' de 'react-native-vector-icons/Icon',
    // lo que permite usar los iconos de FontAwesome como componentes dentro de la aplicación.
    
    import { Icon } from 'react-native-vector-icons/Icon';
    export default Icon;
  }
  